package com.xcompwiz.mystcraft.world.gen.layer;

import net.minecraft.world.WorldType;
import net.minecraft.world.biome.Biome;
import net.minecraft.world.gen.layer.GenLayer;
import net.minecraft.world.gen.layer.IntCache;

public class GenLayerBiomeMyst extends GenLayer {
	private Biome	allowedBiomes[];

	public GenLayerBiomeMyst(long par1, GenLayer par3GenLayer, WorldType par4WorldType, Biome allowedBiomes[]) {
		super(par1);
		parent = par3GenLayer;
		this.allowedBiomes = allowedBiomes;
	}

	/**
	 * Returns a list of integer values generated by this layer. These may be interpreted as temperatures, rainfall amounts, or biomeList[] indices based on the
	 * particular GenLayer subclass.
	 */
	@Override
	public int[] getInts(int par1, int par2, int par3, int par4) {
		int ai1[] = IntCache.getIntCache(par3 * par4);

		for (int i = 0; i < par4; i++) {
			for (int j = 0; j < par3; j++) {
				initChunkSeed(j + par1, i + par2);

				ai1[j + i * par3] = Biome.getIdForBiome(allowedBiomes[nextInt(allowedBiomes.length)]);
			}
		}

		return ai1;
	}
}
